sql.where:
is_delete = 0

    and depart_no like CONCAT('%',#{departNo,jdbcType=VARCHAR},'%')


    and depart_name like CONCAT('%',#{departName,jdbcType=VARCHAR},'%')


    and category_code = #{categoryCode,jdbcType=BIGINT}


    and classify_code = #{classifyCode,jdbcType=VARCHAR}


    and org_no like CONCAT('%',#{orgNo,jdbcType=VARCHAR},'%')


    and org_name like CONCAT('%',#{orgName,jdbcType=VARCHAR},'%')


    and is_enable = #{isEnable,jdbcType=TINYINT}

mapper.select:
select * from (
SELECT row_number () OVER (ORDER BY id DESC) rownum,

FROM depart WITH(NOLOCK)

) tb
WHERE tb.rownum BETWEEN #{page} AND #{size}

select
category_code categoryCode, category_name categoryName, classify_code classifyCode, classify_name classifyName
from depart WITH(NOLOCK)
where is_delete = 0
and org_no = #{orgNo,jdbcType=VARCHAR}
and category_code = #{categoryCode,jdbcType=BIGINT}

select
depart_no departNo, depart_name departName
from depart WITH(NOLOCK)
where is_delete = 0
and org_no = #{orgNo,jdbcType=VARCHAR}
and category_code = #{categoryCode,jdbcType=BIGINT}
and classify_code = #{classifyCode,jdbcType=VARCHAR}
