mapper.sql:
is_delete = 0

    and org_no = #{orgNo,jdbcType=VARCHAR}


    and depart_no = #{departNo,jdbcType=VARCHAR}


    and doctor_no = #{doctorNo,jdbcType=VARCHAR}


     and num_date >= #{numDate}


     and num_date <= #{numDateEnd}

mapper.select:
select * from (
SELECT row_number () OVER (ORDER BY num_date DESC) rownum,

FROM numeral WITH(NOLOCK)



) tb
WHERE tb.rownum BETWEEN #{page} AND #{size}
