sql.where:
is_delete = 0

    and org_no like CONCAT('%',#{orgNo,jdbcType=VARCHAR},'%')


    and org_name like CONCAT('%',#{orgName,jdbcType=VARCHAR},'%')


    and machine_no like CONCAT('%',#{machineNo,jdbcType=VARCHAR},'%')


    and machine_name like CONCAT('%',#{machineName,jdbcType=VARCHAR},'%')


    and factory = #{factory,jdbcType=VARCHAR}


    and model = #{model,jdbcType=VARCHAR}


    and num_receipts = #{numReceipts,jdbcType=VARCHAR}


    and report = #{report,jdbcType=VARCHAR}

mapper.select:
select * from (
SELECT row_number () OVER (ORDER BY id DESC) rownum,

FROM self_machine WITH(NOLOCK)

) tb
WHERE tb.rownum BETWEEN #{page} AND #{size}
